// Push all Midi-QoL targets 15 ft away from the user/caster
// Player-safe for your MAF: MAF.runAsGM(codeString, argsObj) ONLY
// Adds GM-side chat to prove the relay ran

(async () => {
  const SHOVE_FT = 15;
  const lastArg = args.at(-1);
  const RayCls = (foundry?.canvas?.geometry?.Ray) ? foundry.canvas.geometry.Ray : Ray;

  // ----- origin / caster -----
  let originToken;
  if (lastArg?.tokenUuid) {
    originToken = await fromUuid(lastArg.tokenUuid);
    originToken = originToken?.object ?? originToken;
  } else if (lastArg?.tokenId) {
    originToken = canvas.tokens.get(lastArg.tokenId);
  } else if (token) {
    originToken = token;
  }
  if (!originToken) {
    ui.notifications?.warn("Push 15 ft: could not find the activity user.");
    return;
  }

  // ----- Midi-QoL targets -----
  let targetUuids = [];
  if (Array.isArray(lastArg?.targetUuids) && lastArg.targetUuids.length) {
    targetUuids = lastArg.targetUuids;
  } else if (Array.isArray(lastArg?.targets) && lastArg.targets.length) {
    targetUuids = lastArg.targets.map(t => t.document?.uuid ?? t.uuid).filter(Boolean);
  } else if (Array.isArray(lastArg?.hitTargets) && lastArg.hitTargets.length) {
    targetUuids = lastArg.hitTargets.map(t => t.document?.uuid ?? t.uuid).filter(Boolean);
  }
  if (!targetUuids.length) return;

  // ----- grid maths -----
  const scene = canvas.scene;
  const grid = canvas.grid;
  const gridSize = scene.grid.size;
  const gridDistance = scene.grid.distance || 5; // ft per square
  const pxPerFoot = gridSize / gridDistance;
  const shove15px = SHOVE_FT * pxPerFoot;
  const shove10px = 10 * pxPerFoot;
  const shove5px = 5 * pxPerFoot;
  const halfWidth = Math.floor(gridSize / 2);

  function snapToGrid(x, y) {
    if (grid?.getSnappedPoint) {
      const pt = grid.getSnappedPoint({ x, y }, { mode: "closest" });
      if (pt && typeof pt.x === "number" && typeof pt.y === "number") {
        return { x: pt.x, y: pt.y };
      }
    }
    return { x, y };
  }

  function canStandHere(tokenObj, xTL, yTL) {
    const destCentre = {
      x: xTL + tokenObj.w / 2,
      y: yTL + tokenObj.h / 2,
      elevation: tokenObj.document?.elevation ?? 0
    };
    const origin = tokenObj.center;

    const collided = tokenObj.checkCollision(destCentre, {
      origin,
      type: "move",
      mode: "any"
    });
    if (collided) return false;

    const rect = new PIXI.Rectangle(xTL, yTL, tokenObj.w, tokenObj.h);
    const thisElev = tokenObj.document?.elevation ?? 0;
    for (const other of canvas.tokens.placeables) {
      if (other.id === tokenObj.id) continue;
      if ((other.document?.elevation ?? 0) !== thisElev) continue;
      if (other.document.hidden) continue;
      const oRect = new PIXI.Rectangle(other.x, other.y, other.w, other.h);
      if (rect.intersects(oRect)) return false;
    }
    return true;
  }

  async function moveTokenDoc(tokenUuid, x, y) {
    // GM can do it directly
    if (game.user.isGM) {
      const tok = await fromUuid(tokenUuid);
      if (tok?.document) await tok.document.update({ x, y });
      return;
    }

    // Player – use your SIMPLE MAF
    const maf = globalThis.MAF;
    if (maf && typeof maf.runAsGM === "function") {
      // IMPORTANT: must evaluate to a function(args)
      const code =
        "(async function(args){ const fu = (globalThis.fromUuid || fromUuid); const tok = await fu(args.tokenUuid); if(!tok) { ChatMessage.create({content: 'MAF shove: token not found'}); return; } const doc = tok.document || tok; if(!doc) { ChatMessage.create({content: 'MAF shove: no document'}); return; } await doc.update({x: args.x, y: args.y}); })";
      await maf.runAsGM(code, { tokenUuid, x, y });
      return;
    }

    ui.notifications?.warn("Push 15 ft: need GM or MAF to move targets.");
  }

  // ----- shove each target -----
  for (const tgtUuid of targetUuids) {
    const tObj = await fromUuid(tgtUuid);
    const targetToken = tObj?.object ?? tObj;
    if (!targetToken) continue;
    if (targetToken.id === originToken.id) continue;

    const ray = new RayCls(originToken.center, targetToken.center);
    const attempts = [shove15px, shove10px, shove5px];

    for (const shovePx of attempts) {
      let xTL = targetToken.center.x - halfWidth;
      let yTL = targetToken.center.y - halfWidth;

      if (Math.abs(ray.dx) > 0.001) {
        xTL = targetToken.center.x + (ray.dx / Math.abs(ray.dx)) * shovePx - halfWidth;
      }
      if (Math.abs(ray.dy) > 0.001) {
        yTL = targetToken.center.y + (ray.dy / Math.abs(ray.dy)) * shovePx - halfWidth;
      }

      const snapped = snapToGrid(xTL, yTL);

      if (canStandHere(targetToken, snapped.x, snapped.y)) {
        await moveTokenDoc(targetToken.document.uuid, snapped.x, snapped.y);
        break;
      }
    }
  }
})();
