    return true;
  }

  if (globalThis.MAF?.runAsGM) {
    // old/simple MAF: code string evaluates to function(args)
    const code = `
      return async (args) => {
        const doc = await fromUuid(args.uuid);
        if (!doc) return false;
        await doc.update(args.updates, { animate: args.animate });
        return true;
      };
    `;
    return await globalThis.MAF.runAsGM(code, { uuid: tokenDocUuid, updates, animate });
  }

  ui.notifications?.error("Push: Need GM perms (or MAF.runAsGM) to move targets.");
  return false;
}

// ---- distance helpers: prefer measurePath cost ----
function measureCostFt(a, b) {
  try {
    const m = canvas.grid.measurePath([a, b]);
    return m?.cost ?? m?.distance ?? m;
  } catch {
    const px = Math.hypot(a.x - b.x, a.y - b.y);
    return (px / gridSize) * distPerSquare;
  }
