  if (!entry) return null;
  if (entry?.document && entry?.center) return entry;
  if (entry.documentName === "Token") return entry.object ?? canvas.tokens.get(entry.id);
  if (typeof entry === "string") {
    const doc = await fromUuid(entry);
    return doc?.object ?? (doc?.id ? canvas.tokens.get(doc.id) : null);
  }
  const uuid = entry.tokenUuid ?? entry.targetUuid ?? entry.uuid;
  if (uuid) {
    const doc = await fromUuid(uuid);
    return doc?.object ?? (doc?.id ? canvas.tokens.get(doc.id) : null);
  }
  const id = entry.tokenId ?? entry.targetId ?? entry.id;
  return id ? canvas.tokens.get(id) : null;
